<?phpclass MY_Frontend extends CI_Controller{	public function __construct()	{		parent::__construct();		$this->output->enable_profiler(false);	}	function header() { $this->load->view('frontend_view/layout/header'); }	function footer() { $this->load->view('frontend_view/layout/footer'); }}class MY_Backend extends CI_Controller{	function __construct()	{		parent::__construct();		$this->output->enable_profiler(false);		if ($this->is_login() == FALSE)		{			redirect('home/login');		}	}	function header(){ $this->load->view('backend_view/layout/header'); }	function footer(){ $this->load->view('backend_view/layout/footer'); }	function is_login()	{		return TRUE;	}}