<?phpclass MY_Frontend extends CI_Controller{		public function __construct()	{		parent::__construct();		$this->output->enable_profiler(false);	}		function header()	{		$this->load->view('frontend_view/layout/header');		//$this->load->view('template_frontend/slider');	}		function sidebar(){		//$this->load->view('template_frontend/sidebar');	}		function footer()	{		$this->load->view('frontend_view/layout/footer');	}	}class MY_Backend extends CI_Controller{		function __construct()	{		parent::__construct();				$this->output->enable_profiler(false);		if (is_login() == FALSE){			redirect('auth');		}	}		function header(){		$this->load->view('layout_backend/header');		$this->load->view('layout_backend/left');		$this->load->view('layout_backend/content_open');	}	function footer(){		$this->load->view('layout_backend/content_close');		$this->load->view('layout_backend/footer');	}	}